--[[- eigene Zeit einstellen- weitere Tees: Lapacho und Mate-Teehttp://www.heilkraeuter.de/lexikon/lapacho.htmhttp://www.wellness-gesund.info/Artikel/10835.htmltable.insert     (getraenktab, { "Russische Mischung",            300000, 225000, 150000, 95, 1 } )table.insert     (getraenktab, { "Nepal (schwarz)",               360000, 330000, 300000, 95, 1 } )table.insert     (getraenktab, { "Nilgiri (schwarz)",             360000, 330000, 300000, 95, 1 } )table.insert     (getraenktab, { "Keemun (schwarz)",              240000, 210000, 180000, 95, 1 } )table.insert     (getraenktab, { "Yunnan (schwarz)",              240000, 210000, 180000, 95, 1 } )table.insert     (getraenktab, { "Java (schwarz)",                180000, 150000, 120000, 95, 1 } )--]]display.setStatusBar( display.HiddenStatusBar ) slider = require "slider"json = require "json"widget = require "widget"widget.setTheme( "theme_ios" )local ads = require "ads"screenW, screenH = display.contentWidth, display.contentHeight-- Für den Timertimerzeit  = 0jetzt      = 0biswann    = 0timing     = falselasttab    = 1local filePath = system.pathForFile( "data.txt", system.DocumentsDirectory )-- Einstellungenconfig = { vibration=1, countdown=1, sectick=0, alarmton=0, diesprache="de", sorte=2 }-- Anzeigen und Datentoene       = { "klassik_wecker.mp3", "hahnenschrei.mp3", "elektronik_wecker.mp3", "dampflok.mp3", "teekessel.mp3" }tictac      = { "tictac.mp3" }count       = { "countdown.mp3" }uhrlaeuft   = 0     -- 0 = nichts, 1 = läuftgetraenktab = {}function switch(c)  local swtbl = {    casevar = c,    caseof = function (self, code)      local f      if (self.casevar) then        f = code[self.casevar] or code.default      else        f = code.missing or code.default      end      if f then        if type(f)=="function" then          return f(self.casevar,self)        else          error("case "..tostring(self.casevar).." not a function")        end      end    end  }  return swtblendfunction split (str, pat)   local t = {}   local fpat = "(.-)" .. pat   local last_end = 1   local s, e, cap = str:find(fpat, 1)   while s do      if s ~= 1 or cap ~= "" then	 table.insert(t,cap)      end      last_end = e+1      s, e, cap = str:find(fpat, last_end)   end   if last_end <= #str then      cap = str:sub(last_end)      table.insert(t,cap)   end   return t  end-- Daten auslesen oder speichernfunction einstellung_speichern ()	local t  = json.encode ( config )	local tf = system.pathForFile( "user.data", system.DocumentsDirectory )	local rtf = io.open( tf, "wb" )	if  not rtf then print( "writeFileName open error!" )		results = false                 -- error	else		if not rtf:write( t ) then   			print( "write error!" )         	results = false -- error	    end	end	rtf:close()endfunction einstellung_laden ()	local path = system.pathForFile( "user.data", system.DocumentsDirectory )	local file = io.open( path, "r" )	if file then	-- user.data gibt es		io.close( file )		local jsonFile = function( filename, base )		if not base then base = system.DocumentsDirectory end	-- set default base dir if none specified			local path = system.pathForFile( filename, base )		-- create a file path for corona i/o			local contents											-- will hold contents of file			local file = io.open( path, "r" )						-- io.open opens a file at path. returns nil if no file found			if file then											-- read all contents of file into a string			   contents = file:read( "*a" )			   io.close( file )										-- close the file after using it			end			return contents		end		config = json.decode( jsonFile( "user.data", system.DocumentsDirectory ) )	else		einstellung_speichern ()	endendfunction funktionwahl ( func, valu )  if ( valu ~= 0 ) then valu = 1 end  if ( func == "vib" ) then config.vibration = valu end  if ( func == "sec" ) then config.sectick   = valu end  if ( func == "cou" ) then config.countdown = valu end  einstellung_speichern ()endfunction sortenwahl ( sor )  config.sorte = sor  einstellung_speichern ()  hole_tabbar ( )endfunction tonwahl ( valu )  config.alarmton = valu  einstellung_speichern ()endfunction string:split(sep)  local sep, fields = sep or ":", {}  local pattern = string.format("([^%s]+)", sep)  self:gsub(pattern, function(c) fields[#fields+1] = c end)  return fieldsendfunction SekundenInUhrzeit(millisecs)  local Seconds = millisecs / 1000  local Minutes = math.floor(Seconds / 60)  local Seconds = Seconds - Minutes * 60  if (string.format("%02.f", Seconds) == "60") then Minutes = Minutes + 1; Seconds = 0 end  return (string.format("%02.f", Minutes)) .. ":" .. (string.format("%02.f", Seconds))end--- sind noch ein paar mehr...function mache_tee ( )  if (getraenktab ~= nil) then getraenktab = nil endgetraenktab = { }--  	table.insert   ( getraenktab, { name = schwarzertee, ziehzeit = 0 } )                table.insert   ( getraenktab, { name = "Assam (Indien)", nameen="Assam (India)", ziehzeit = 240000, datei = "assam", temperatur = 100,				kurzde = "Kräftiger, voller Schwarztee", kurzen = "Strong, full, black tea",                beschreibungde = "Geschmack:\nkräftig, voll, rund und malzig; leuchtet aufgebrüht in einem dunklen Rotbraun.\n\nGeschichte:\nDer Tee wurde im 19. Jahrhundert entdeckt. Er verdankt seinen Namen seinem Herkunftsort – der indischen Assam-Region, direkt zwischen den Grenzen von Burma und Bangladesch gelegen. Er wird zweimal jährlich geerntet.\n\nWissenswert:\nAssam Tee dient als Grundlage für englische und irische Frühstücksteemischungen sowie für Ostfriesentees.\n\n",                beschreibungen = "Taste:\nstrong, full, round, malty; with a slightly dark redbrown color.\n\nHistory:\nThis Tea was discovered in the 19th century and is named after the territoy it origins - the Indian region of Assam, located directly to the borders to Burma and Bangla Desh. It is harvested twice a year.\n\nNice to know:\nAssam Tea is the foundation of English or Irish Breakfast-Tea as well as for the famous German Ostfriesentee (East Frisian Tea).\n\n" } )table.insert   ( getraenktab, { name = "Ceylon (Sri Lanka)", nameen="Ceylon (Sri Lanka)", ziehzeit = 120000, datei = "ceylon", temperatur = 100,				kurzde = "Mittelkräftiger Schwarztee", kurzen = "Semi-robust black tea",                beschreibungde = "Geschmack:\nmittelkräftig, herb, leicht malzig und zitronig-frisch; färbt sich nach dem Aufguss golden.\n\nGeschichte:\nEtwa 1870 gingen dem schottischen Farmer James Taylor auf Grund einer Rostpilzseuche die Kaffeepflanzen ein. Daher versuchte er es mit Tee – und hatte Erfolg. Heute gehört Sri Lanka zu den größten Teeproduzenten der Welt.\n\nWissenswert:\nCeylon-Tee schmeckt am besten wenn er aus losen Teeblättern zubereitet wird.\n\n",                beschreibungen = "Taste:\nA semi-robust tea, bitter, slightly malty and lemony-fresh; gives a golden colored cup.\n\nHistory:\nIn about 1870, Scottish farmer James Taylor lost his coffee plants due to rust fungus. Hence, he tried Tea - and was successfull. Today, Sri Lanka is one of the biggest producers of Tea.\n\nNice to know:\nCeylon-Tea tastes best if made of tealeaves, not from tea-bags.\n\n" } )table.insert   ( getraenktab, { name = "Darjeeling (Indien)", nameen="Darjeeling (India)", ziehzeit = 180000, datei = "darjeeling", temperatur = 100,				kurzde = "Feiner, milder Schwarztee", kurzen = "Fine, mild black tea",                beschreibungde = "Geschmack:\nsehr fein, mild und blumig, bei der Sorte First Flush auch leicht bitter.\n\nGeschichte:\nDer Teeanbau in Darjeeling, im indischen Bundesstaat Westbengalen nahe des Himalaya begann in der britischen Kolonialzeit. Die Plantagen befinden sich in etwa 2000 Metern Höhe.\n\nWissenswert:\nDiese Sorte eignet sich auf Grund ihres hohen Gerbstoffanteils nicht für den Aufguss mit hartem Wasser.\n\n",                beschreibungen = "Taste:\nFine, mild, and bit flower. First Flush can be slightly bitter.\n\nHistory:\nGrowing tea in Darjeeling, Indian Federal State West Bengal near the Himalayas started during the British colonial-time. Fields are 2000 Metres above sea level.\n\nNice to know:\nBecause of the high amount of tanning agent, do not use hard water to make Darjeeling.\n\n" } )table.insert   ( getraenktab, { name = "Earl Grey", nameen="Earl Grey", ziehzeit = 240000, datei = "earlgrey", temperatur = 100,				kurzde = "Erfrischende Schwarzteemischung", kurzen = "Refreshing black tea blend",                beschreibungde = "Geschmack:\nleicht erfrischend durch das in ihm enthaltene Bergamottöl.\n\nGeschichte:\nBenannt nach dem britischen Premiermenister Lord Charles Grey II. Einer Legende nach wurde die berühmte Mischung während eines Sturmes auf hoher See entdeckt: Durch den starken Wind aus den Befestigungen gerissen, landete Bergamotteöl auf den mitgeführten Teeballen. Der Lord probierte die neue Mischung und verliebte sich gleich in das feine Aroma.\n\nWissenswert:\nEarl Grey wird teilweise aus unterschiedlichen Sorten Darjeeling gemischt. Er wird auch zum Zubereiten von Eistee verwendet.\n\n",                beschreibungen = "Taste:\nRefreshing because of the Bergamot-Oil.\n\nHistory:\nBritish Prime Minister Lord Charles Grey II. discovered this blend during a storm far offshore. The strong winds let Bergamot-Oil spill over the tea-bales. The Lord tried the blend and fell in love with its fine flavor.\n\nNice to know:\nEarl Grey is a blend of differente types of Darjeeling and is used for Icetea.\n\n" } )table.insert   ( getraenktab, { name = "Irische Mischung", nameen="Irish Blend", ziehzeit = 240000, datei = "irish", temperatur = 100,				kurzde = "Klassische Irische Mischung", kurzen = "Classic Irish Blend",                beschreibungde = "Geschmack:\nleicht erfrischend und vollmundig; aufgebrüht leuchtet er goldbraun.\n\nGeschichte:\nHierbei handelt es sich um eine klassische Schwarzteemischung, die meist aus Assam und Darjeeling besteht.\n\nWissenswert:\nIrish Blend schmeckt besonders gut, wenn er leicht gezuckert mit einem Schuss Milch/einem Spritzer Zitrone getrunken wird.\n\n",                beschreibungen = "Taste:\nSlightly refreshing and full-bodied, golden brown.\n\nHistory:\nThe classic black-tea-blend of Assam and Darjeeling.\n\nNice to know:\nTry with a bit of sugar and fresh milk or some lemon-juice.\n\n" } )                                table.insert   ( getraenktab, { name = "Lapsang Souchong (正山小種)", nameen="Lapsang Souchong (正山小種)", ziehzeit = 240000, datei = "lapsang", temperatur = 100,				kurzde = "Rauchtee aus China", kurzen = "Smoked tea from China",                beschreibungde = "Geschmack:\nGetreu seines Namens besitzt er ein rauchig-würziges Aroma. In der Tasse scheint er kupferrot.\n\nGeschichte:\nein großblättriger Schwarztee. Er welkt nach der Ernte und wird dann über brennendem Kiefernholz geräuchert. Russische Karawanenteemischungen enthalten Rauchtee. Sie erinnern damit an die Zeit, in der die mit Tee beladenen Pferde, Kamele und Planwagen dem Rauch der Lagerfeuer ausgesetzt waren.\n\nWissenswert:\nWird auch nach längerem Ziehen nicht bitter und eignet sich somit für die Verwendung im Samowar.\n\n",                beschreibungen = "Taste:\nGives a smoky and spicy aroma. The cup-color is copperred.\n\nHistory:\nA big leafed Black Tea. Withers after harvest, and is smoked above burning pinewood. Russian Caravan-Tea holds Lapsang Souchong. It reminds of the time, when tea-laden horse-drawn carawans where resting near burning fires. The smoke would then flavor the tea-leafs.\n\nNice to know:\nDoes not get bitter, even after brewing it a long time - this way, it can be used in Samowars.\n\n" } )     table.insert   ( getraenktab, { name = "Türkischer Tee", nameen="Turkish Tea", ziehzeit = 600000, datei = "turkish", temperatur = 100,				kurzde = "Klassischer Türkischer Tee", kurzen = "Classic Turkish Tea",                beschreibungde = "Geschmack:\nstark, recht bitter. Farblich ähnelt er dem Ceylontee.\n\nGeschichte:\nDer Türkische Tee wird auch Çay - gesprochen „Tschai“ - genannt. Die Türken bauen ihn wegen der guten klimatischen Bedingungen in der Region Rize an der Schwarzmeerküste sowie an der Grenze zu Georgien und Armenien an.\n\nWissenswert:\nAm besten im Samowar zubereiten und mit viel Zucker trinken.\n\nZiehzeit:\nbis zu 15 Minuten.",                beschreibungen = "Taste:\nstrong, bitte. Similar color as the Ceylon.\n\nHistory:\nTurkish Tea is called Cay. Because of the good climate, Cay is planted in region Rize near the coast of the Black Sea, as well as at the frontier to Georgia and Armenia.\n\nNice to know:\nUse a Samowar, and enjoy with lots of sugar.\n\nBrewing Time:\nUp to 15 minutes." } )                           table.insert   ( getraenktab, { name = gruenertee, ziehzeit = 0 } )table.insert   ( getraenktab, { name = "Bancha (番茶, Japan)", nameen="Bancha (番茶,Japan)", ziehzeit = 110000, datei = "bancha", temperatur = 75,				kurzde = "Frischer, leicht grasiger Grüntee", kurzen = "Fresh, slighty grassy green tea",                beschreibungde = "Geschmack:\nfrisch und leicht grasig; leuchtet in der Tasse hellgrün.\n\nGeschichte:\n'Ban' bedeutet übersetzt so viel wie 'grob, gewöhnlich'. Bancha stammt aus der Nähe von Shizuoka in Japan und besteht aus den Blättern, die nach der Pflückung des Senchas neu nachwachsen. Der Tee ist reich an Gerbstoffen.\n\nWissenswert:\nBeim Aufgießen eines Bancha sollte das Wasser etwa 70 - 80 °C haben. Der Tee enthält wenig Koffein und wird daher auch gerne von Kindern getrunken.\n\n",                beschreibungen = "Taste:\nfresh, slighty grassy; cup-color is light green.\n\nHistory:\n'Ban' means common or gross. This Tea determines from near Shizuoka in Japan, and its leafs regrow after the harvest. Bancha has lots of tanning agent.\n\nNice to know:\nUse water of about 70 - 80 °C. Bancha holds only little Caffeine, and is more like an everday-tea. It is mostly consumed by childen.\n\n" } )                table.insert   ( getraenktab, { name = "Chun Mee (珍眉, China)", nameen="Chun Mee (珍眉, China)", ziehzeit = 110000, datei = "chunmee", temperatur = 90,				kurzde = "Frisch-herber Grüntee", kurzen = "Fresh, slightly bitter",                beschreibungde = "Geschmack:\nfrisch-herb, manchmal auch leicht rauchig; schimmert in der Tasse gelblich.\n\nGeschichte:\nDiese Sorte stammt aus unterschiedlichen Anbaugebieten, kommt aber hauptsächlich aus China. Der Name 'Chun Mee' bedeutet so viel wie 'wertvolle Augenbraue'. Der Tee trägt diesen Namen, weil seine Blätter von der Form her an kleine Augenbrauen erinnern.\n\nWissenswert:\nDas Wasser sollte beim Aufgießen eines Chun Mee Tees etwa 90 °C haben. Besonders gut schmeckt diese Teesorte in Kombination mit Käse (Gorgonzola, Camenbert), Fisch und Meeresfrüchten.\n\n",                beschreibungen = "Taste:\nfresh and slightly bitter, a bit smoky with a yellow cup-color.\n\nHistory:\nThis tea determines from different plantations, but mainly from China. The name 'Chun Mee' means as much as 'valuable eyebrow'. This is because the leafs remind of little eyebrows.\n\nNice to know:\nUse water of 90 °C temperature. Try this tea with Cheese (Gorgonzola, Camenbert), Fish or 'Frutti di Mare'.\n\n" } )                table.insert   ( getraenktab, { name = "Gun Powder (珠茶, China)", nameen="Gun Powder (珠茶, China)", ziehzeit =  60000, datei = "gunpowder", temperatur = 85,				kurzde = "Herber und kräftiger Grüntee", kurzen = "Strong, slightly bitter green tea",                beschreibungde = "Geschmack:\nherb und kräftig, leicht bitter; dunkelgelbe Tassenfarbe.\n\nGeschichte:\nEr stammt vorwiegend aus der chinesischen Provinz Zhejiang, wird aber auch in Taiwan angebaut und mehrheitlich per Hand gepflückt. Sein Name bedeutet „Schießpulver“, da seine Blätter in ihrer Form kleinen Schrotkugeln ähneln.\n\nWissenswert:\nBeim Aufgießen sollte das Wasser etwa 80 - 90 °C haben. Generell gilt: Je kleiner die Kugeln, desto besser der Tee. Gunpowder enthält von allen Grüntees den höchsten Koffeinanteil.\n\n",                beschreibungen = "Taste:\nStrong, slightly bitter, dark-yellow color.\n\nHistory:\nDetermines from the Chinese province Zhejiang, but is also grown in Taiwan. This tea is manually harvested, and is named after the 'Gun-Powder' for weapons: The leafs remind of gun-pellets.\n\nNice to know:\nUse boiled water of app. 80 - 90 °C. The smaller the tea-pellet, the better. Gunpowder holds the most caffeine of all green teas.\n\n" } )                table.insert   ( getraenktab, { name = "Gyokuro (玉露, Japan)", nameen="Gyokuro (玉露,Japan)", ziehzeit = 110000, datei = "gyokuro", temperatur = 50,				kurzde = "Leicht süßer Grüntee", kurzen = "Slightly sweet green tea",                beschreibungde = "Geschmack:\nenthält viel Süße und erinnert leicht an den Geschmack von Spinat.\n\nGeschichte:\n'Gyokuro' bedeutet so viel wie 'edler Tautropfen'. Er stammt aus dem Uji-Distrikt bei Kyoto und ist einer der edelsten Teesorten Japans. Das Besondere: Diese Sorte wird vor der Ernte mit Netzen oder Matten aus Bambus oder Schilf abgedeckt, wodurch die vermehrte Bildung von Gerb- und Bitterstoffen verhindert wird.\n\nWissenswert:\nDas Wasser darf beim Aufgießen nur 40 - 60 °C haben, damit der Tee nicht bitter wird. Sein Preis kann bis zu 100 Euro pro 100 Gramm betragen – einer der teuersten Grüntees der Welt.\n\n",                beschreibungen = "Taste:\nSweet, reminds of spinach.\n\nHistory:\n'Gyokuro' means 'Noble Dewdrop'. It determines from Uji-Distrikt near Kyoto. Different to other green teas: The trees are covered with nets or matting from Bamboo or reed. This reduces the amount of tanning agents and reduces bitterness.\n\nNice to know:\nUse boiled water of app. 40 - 60 °C. The prize: About 100 Euros per 100 Grams - one of the most expensive green teas available.\n\n" } )table.insert   ( getraenktab, { name="Hōjicha (ほうじ茶, Japan)", nameen="Hōjicha (ほうじ茶, Japan )", ziehzeit = 30000, datei = "gruen", temperatur = 90,				kurzde = "Gerösteter Grüntee", kurzen = "Roasted Green Tea",                beschreibungde = "Geschmack:\nmildes, malziges und teilweise leicht nussiges Aroma\n\nGeschichte:\nIm japanischen Kyoto vor 80  Jahren erfunden, wird er hauptsächlich dort produziert – meist aus Sencha- oder Bancha-Blättern. .\n\nWissenswert:\nBesitzt nur wenig Koffein und gilt als magenfreundlich. Zum Zubereiten mit heißem Wasser aufbrühen.",                beschreibungen = "Taste:\nmild, malty, some sent of nuts.\n\nHistory: Invented in Kyoto, Japan a mere 80 years ago, Hōjicha is still produced there. Mostly made from Sencha- or Bancha-leafs.\n\nNice to know:\Has only a small amount of caffeine and is gentle on the stomach. Brew with hot water." } )table.insert   ( getraenktab, { name = "Jasmintee (茉莉花茶, China)", nameen="Yasmin Tea (茉莉花茶, China)", ziehzeit = 120000, datei = "jasmin", temperatur = 80,				kurzde = "Grüntee mit Jasmin", kurzen = "Green Tea with Yasmin",                beschreibungde = "Geschmack:\nliebliches, starkes Jasminaroma; leicht gelbliche Tassenfarbe\n\nGeschichte:\nEr besteht meist aus Grüntee oder Oolong-Tees, gewürzt mit echten Jasminblüten oder bedampft über einem Jasminblütenbad. Sein Ursprung liegt in der chinesischen Song-Dynastie – etwa 960 bis 1279.\n\nWissenswert:\nNicht zu spät abends trinken. Es heißt, der Tee könne die Sinne betören, anregen aber auch beruhigen. Wird ähnlich Grüntee zubereitet – dabei am besten gefiltertes Wasser verwenden. Mehrfaches Aufgießen möglich.\n\nZiehzeit:\n2 bis 4 Minuten",                beschreibungen = "Taste:\nLovely, strong sent of Yasmin, slightly yello color.\n\nHistory:\nIs based on Green Tea or Oolong, spiced with real Yasmin flowers. Or steamed above Yasmin flower brew.\n\nNice to know:\nDo not consume late in the evening. They say, this tea would misguide the sens, stimulate, and calm down. Brew like a Green Tea - prefer to use filtered water. You can brew the tea several times.\n\nBrewing Time: 2 to 4 minutes." } )                table.insert   ( getraenktab, { name = "Matcha (抹茶, Japan)", nameen="Matcha (抹茶, Japan)", ziehzeit = 100000, datei = "matcha", temperatur = 60,				kurzde = "Leicht süßer, pulveriger Grüntee", kurzen = "Slightly sweet, powdered green tea",                beschreibungde = "Geschmack:\nleicht süßlich; in der Tasse kräftig Grün .\n\nGeschichte:\nZu feinem Pulver verarbeiteter Grüntee, der häufig in der japanischen Teezeremonie getrunken und mit einem Bambusbesen schaumig geschlagen wird. Erste Zubereitungen fanden in China statt und wurden hauptsächlich von Mönchen praktiziert. Heute darf Matcha ausschließlich in Japan hergestellt werden.\n\nWissenswert:\nDie Wassertemperatur sollte beim Aufgießen etwa 60 °C betragen. Diese Sorte enthält viel Vitamin A, Vitamine der B-Gruppe, Vitamin C, Vitamin E sowie Karotin.\n\n",                beschreibungen = "Taste:\nslighty sweet, strong green color.\n\nHistory:\nThis green tea is powdered, and gets foamy when stirred with a broom of bamboo. It is used during the famous Japanese tea-ceremony. First preparations date back to China and were done by Monks. Today, Matcha must not be produced outside of Japan.\n\nNice to know:\nUse soft, boiled water of about 60 °C. This type has a lot of Vitamins: A, some of the B-group, C, E and Carotene.\n\n" } )                table.insert   ( getraenktab, { name = "Pu-Erh Tee (普洱茶, China)", nameen="Pu-Erh Tea (普洱茶, China)", ziehzeit = 240000, datei = "puerh", temperatur = 100,				kurzde = "Grüntee aus Pu-Erh", kurzen = "Green Tea from Pu-Erh",                beschreibungde = "Geschmack:\nkräftig, erdig. Besitzt eine dunkle, rötliche Farbe.\n\nGeschichte:\nEr zählt zu den ältesten Sorten, stammt aus der chinesischen Provinz Yunnan und wurde nach seinem Herkunftsort, der Stadt Pu-Erh, benannt. Seit etwa 1.700 Jahren wird er dem Kaiser traditionell als erlesenes Präsent geboten.\n\nWissenswert:\nIm Gegensatz zu anderen Grün- und Schwarztees gewinnt er durch Alterung an Qualität und Geschmack. Pro Tasse reicht ein Teelöffel Pu-Erh Tee. Aufbrühen erfolgt mit kochendem Wasser. Schmeckt gut mit Honig oder Ahornsirup.\n\nZiehzeit:\n3 bis 5 Minuten",                beschreibungen = "Taste:\nStrong, earthy. Has a dark, red color.\n\nHistory:\nPu-Erh tea is one of the oldest teas, destined from Chinese region Yunnan, and is named after the City of Pu-Erh. Traditionally, and for more than 1.700 years, the Chinese Emperor is given Pu-Erh as a special gift by visitors.\n\nGreen or Black teas lose quality and taste during the time. Not Pu-Erh Tea, which will get even better. One spoon is enough for a cup. Use boiling water, and try sweetening with Honey or Maple Syrup.\n\nBrewing Time: 3 to 5 minutes." } )                table.insert   ( getraenktab, { name = "Sencha (煎茶, Japan)", nameen="Sencha (煎茶, Japan)", ziehzeit = 180000, datei = "sencha", temperatur = 75,				kurzde = "Leicht bitterer, frischer Grüntee", kurzen = "Slightly bitter, freh green tea",                beschreibungde = "Geschmack:\nleicht herb und frisch, etwas süß; Tassenfarbe: ein gelblich-helles Grün\n\nGeschichte:\nSencha, der 'gedämpfte Tee', wird am häufigsten in Japan produziert und getrunken. Andere Herstellungsländer sind Taiwan und China. Seine Blätter erinnern in ihrer Form an kleine Nadeln.\n\nWissenswert:\nAufgießen am besten mit 90 °C heißem Wasser - hochwertigen Sencha aber nur mit Wasser, das 75 °C warm ist. Generell gilt: Je dunkler das Blatt, desto besser der Sencha.",                beschreibungen = "Taste:\nSlighty bitter, fresh, sweet. Gives a yellowish-green color.\n\nHistory:\nSencha, 'Steamed Tea', is produced and drunk the most in Japan. But also Taiwan and China grow Sencha. The tealeafs remind of small needles.\n\nNice to know:\nUse water of 90 °C, for high-quality Sencha only 75 °C. The darker the leaf, the better the Sencha." } )table.insert   ( getraenktab, { name="Weißer Tee (白茶, China)", nameen="White Tea (白茶, China)", ziehzeit = 120000, datei = "weiss", temperatur = 70,				kurzde = "Spezielle Sorte des Grüntees", kurzen = "Special Type of Green Tea",                beschreibungde = "Geschmack:\nJe nach Aufguss und Sorte mild bis aromatisch\n\nGeschichte:\nDer Name kommt vom weißen, seidenartigen Flaum, der die jungen Teeknospen umschließt. Für die hochwertigen Sorten wie etwa Yin Zhen werden nur ungeöffnete Blattknospen verwendet – bis zu 30.000 pro Kilo.\n\nWissenswert:\nEr wird wie Grüntee zubereitet – kein kochendes Wasser verwenden. Mehrere Aufgüsse lohnen sich, da jeder etwas anders schmeckt.\n\nZiehzeit: 1 bis 4 Minuten.",                beschreibungen = "Taste:\nDepends on the type, mild or aromatic.\n\nHistory: The name comes from the white, silky fuzz on the young teadbuds. For the higher qualities such as the YinZhen, only closed leaf buds are used - up to 30.000 per kg.\n\nNice to know:\nBrew this tea like Green Tea - do not use boiling water. Multiple infusions are recommended, as they will taste a bit differently.\n\nBrewing time: 1 to 4 minutes." } )                table.insert   ( getraenktab, { name = verschiedenetees, ziehzeit = 0 } )table.insert   ( getraenktab, { name = "Früchtetee", nameen="Fruit infusion", ziehzeit = 450000, datei = "fruechtetee", temperatur = 90,				kurzde = "Klassischer Früchtetee", kurzen = "Classic fruits infusion",                beschreibungde = "Geschmack:\nabhängig von der jeweiligen Mischung; hauptsächlich rote Tassenfarbe\n\nGeschichte:\nDieses Aufgussgetränk besteht meist aus einer Grundmischung (Apfel, Hagebutte, Hibiskus), die je nach Saison mit weiteren Früchteteilen oder ätherischen Ölen angereichert wird. Die verwendeten Früchte stammen häufig aus Saft- oder Obstkonservenproduktionen.\n\nWissenswert:\nSchmeckt sowohl heiß als auch kalt. Beliebteste Früchte: getrocknete Äpfel, Zitronen- oder Orangenschalen, Hagebutten, Schwarze Johannisbeeren, Hibiskusblüten.\n\nZubereitung: 5 bis 10 Minuten ziehen lassen",                beschreibungen = "Taste:\nDepends on the blend; red cup-color.\n\nHistory:\This infusion uses a basic blend (Apple, Rose Hip, Hibiscus), which is seasonally varied with fruits or essential oils. The fruits often come from juice- or canned-fruit-production.\n\nNice to know:\nCan be consumed hot or chilled. Favorites are dried apples, lemon- or orange peel, rose hips, black currant, hibiscus. Brew for 5 to 10 minutes.\n\n" } )                table.insert   ( getraenktab, { name = "Kräutertee", nameen="Herbal infusion", ziehzeit = 600000, datei = "kraeuter", temperatur = 90,				kurzde = "Klassischer Kräutertee", kurzen = "Classic herb infusion",                beschreibungde = "Geschmack:\nunterschiedlich - kommt auf die jeweiligen Kräuter und Pflanzenanteile an; meist gelbe bis grüne Tassenfarbe.\n\nGeschichte:\nKräutertee ist eigentlich kein Tee sondern ein Aufgussgetränk, das aus Kräutern und unterschiedlichen aromatischen Teilen einzelner Pflanzen besteht. Traditionell wird er zur Linderung von Beschwerden im Verdauungstrakt oder der Atemwege eingesetzt.\n\nWissenswert:\nEr enthält kein Koffein und kann in beliebigen Mengen getrunken werden. Beliebteste Kräuter: Brennnessel, Fenchel, Kamille, Lindenblüte, Pfefferminze und Salbei.\n\n",                beschreibungen = "Taste:\nDepends on the herbs or flowers; yellow to green color.\n\nHistory:\Herbal Tea actually is not a tea but an infusion, containing herbs or aromatic parts of plants and flowers. Traditionally Herbal Tea is used to improve digestive tract or respiratory system.\n\nNice to know:\nHerbal Tea does not hold caffeine and may be consumed in great quantities. Favorites are stinging nettle, fennel, camomile, lime-tree blossom, peppermint, and sage.\n\n"  } )table.insert   ( getraenktab, { name = "Lapacho", nameen="Lapacho", ziehzeit = 900000, datei = "rooibos", temperatur = 100,				kurzde = "Lapacho", kurzen = "Lapacho",                beschreibungde = "Geschmack:\nerdig, leicht süßliche Vanille-Note – ähnlich Rotbuschtee.\n\nGeschichte:\nDer Lapacho-Baum kann bis zu 700 Jahre als werden. Seine Innenrinde verwendeten bereits die Inka zur Herstellung von Tee. Der 'Baum des Lebens' wächst u.a. in den Regenwäldern von Argentinien, Paraguay und Brasilien und soll das Immunsystem stärken und antibakterielle Wirkung haben.\n\nWissenswert:\nInnerlich sollte der koffeinfreie Lapacho nicht überdosiert und maximal sechs Wochen am Stück getrunken werden. Die Wirkung gegen Krebs ist sehr umstritten. Äußerliche Anwendung: u. a. in Form von Bädern oder Kompressen.\n\nZubereitung:\n2 TL Rinde auf 1 Liter Wasser für fünf Minuten aufkochen. Ziehzeit: 15 Minuten.",                beschreibungen = "Taste:\nEarthy, slightly sweet hint of vanilla - similiar to Rooibos.\n\nHistory:\The Lapacho-tree can grow up to 700 years. The Inca used it's inner bark to brew tea for healing purposes. Indians called the tree 'Tree of life'. It grows e.g. in the rainforests of Argentinia, Paraguy, or Brazil, and aims to strengthen the immunine system and has anti-bacterial effect.\n\nNice to know:\nLapacho should be drunk in low dose, and no longer than 6 weeks. Contains no caffeine, and its effect against cancer is very controversial. External use: eg in the form of baths or compresses.\n\nPreparation:\nCook 2 teaspoons of bark in 1 Liter water for about 5 minutes. Let brew for about 15 minutes."  } )table.insert   ( getraenktab, { name = "Mate", nameen="Mate", ziehzeit = 300000, datei = "bancha", temperatur = 80,				kurzde = "Mate", kurzen = "Mate",                beschreibungde = "Geschmack:\npflanzlich bis grasartig - ähnlich Grünem Tee.\n\nGeschichte:\nMate wird aus kleingeschnittenen trockenen Blättern des gleichnamigen Strauchs hergestellt. Einer Legende nach stammen die Blätter sowie das Materezept vom Gott der Guarani-Indianer. Daher erhielt der Tee von den Ureinwohnern Südamerikas die Namen 'Grünes Gold' oder 'Trank der Götter'.\n\nWissenswert:\nIn Brasilien, Argentinien oder Uruguay trinken die Menschen zu jeder Tageszeit Mate-Tee. Er soll gleichzeitig entspannend sowie inspirierend wirken und u. a. gegen Hungergefühl, Asthma und Entzündungen helfen. Mehrere Aufgüsse möglich.\n\nZubereitung:\nAufgegossen wird nun mit 70 bis 95 °C heißem Wasser – je nach Region und Geschmack. Ziehzeit etwa 5 Minuten.",                beschreibungen = "Taste:\nplanty or grassy - similar to Green Tea. Cup-color is yellowish-green.\n\nHistory:\Mate is made from finely chopped dry leaves of the same shrub. According to legend, the leaves and the recipe of Mate come from the God of the Guarani Indians. Therefore, the tea has been given the name 'Green gold' or 'drink of the gods' by the natives of South America.\n\nNice to know:\nIn Brazil, Argentina and Uruguay, people drink mate tea all over the day. It is also intended to be relaxing and inspiring and helping others to hunger, asthma and inflammation. Multiple infusions are possible.\n\nPreparation:\nPrepare with  hot water 70 to 95 ° Celsius - depending on the region and taste. Brewing time is about 5 minutes."  } )table.insert   ( getraenktab, { name = "Oolong (烏龍茶, China)", nameen = "Oolong (烏龍茶, China)", ziehzeit = 180000, datei = "oolong", temperatur = 85,				kurzde = "Blumig-intensiver Oolong", kurzen = "Bloomy-intensive Oolong",                beschreibungde = "Geschmack:\nje nach Sorte blumig, leicht nussig oder intensiv-duftend.\n\nGeschichte:\nEin Tee, der durch eine spezielle Herstellung zwischen dem unfermentierten Grünen und dem voll fermentierten Schwarzen Tee liegt. Oolong bedeutet im Deutschen so viel wie „schwarzer Drache“ oder „schwarze Schlange“. Laut einer chinesischen Legende entstand der Name dadurch, dass ein Bauer bei der Teeblätterernte vor einer schwarzen Giftschlange erschrak und flüchtete.\n\nWissenswert:\nOolong kann mehrmals aufgegossen werden ohne seine Qualität zu verlieren und soll angeblich beim Abnehmen helfen.\n\n",                beschreibungen = "Taste:\nDepends on the type, bloomy, or intensive-aromatic.\n\nHistory:\nThis type of tea is specially treated, and lies in between the not fermented green teas, and the fully roasted black tea. Oolong means 'Black Dragon' or 'Black Snake'. According to Chinese legends,  the name was invented by farmers being frightened by black poisonous Snakes during the tea-harvest.\n\nNice to know:\nThe same tea can be used several times without decrease of quality. They say, it helps losing weight.\n\n" } )                table.insert   ( getraenktab, { name="Rooibos", nameen="Rooibos", ziehzeit = 450000, datei = "rooibos", temperatur = 100,				kurzde = "Klassischer Rotbuschtee", kurzen = "Classic Rooibos",                beschreibungde = "Geschmack:\nfruchtiges Aroma; meist rot-braune Tassenfarbe\n\nGeschichte:\n1722 wurde er erstmals vom Botaniker Carls Thunberg erwähnt. Sein genaues Alter lässt sich nicht belegen. Das teeähnliche Aufgussgetränk besteht aus Pflanzenteilen des gleichnamigen Strauches. Dieser wächst nur in den Zederbergen Südafrikas Die Wörter 'rooi' und 'bos' stammen aus der Sprache Afrikaans und bedeuten soviel wie 'roter Busch'.\nWissenswert:\n\nRooibos enthält kein Koffein. In Südafrika gilt Rooibos als Nationalgetränk.\n\nZubereitung: 5 bis 10 Minuten ziehen lassen.\n\n",                beschreibungen = "Taste:\nfruity aroma, red-brown color\n\nHistory:\nIn 1722 Botany Carls Thunberg wrote about Rooibos. It's distinct age kann not be said. Rooibos holds parts of plants of the bush of the same name. This bush only grows in the Ceddarmountains of South Africa. The words 'rooi' and 'bos' come from the Afrikaans-language and mean 'red bush'.\nNice to know:\n\nRooibos holds no caffeine and is the 'national drink' of South Africa. Brew for 5 to 10 minutes.\n\n"  } )table.insert   ( getraenktab, { name="Zhuke-Teezopf", nameen="Zhuke-Teebraid", ziehzeit = 10000, datei = "zhuke", temperatur = 90,				kurzde = "Ein-Blatt-Tee", kurzen = "One Leaf Tea",                beschreibungde = "Geschmack:\nleicht bitter, rauchig, später erdig, Farbe: intensiv rötlich braun\n\nGeschichte:\nEr ähnelt dem Pu-Erh-Tee und wird in frische Bananenblätter gebunden. Diese Teekultur stammt aus dem chinesischen Zhuang Yuan Fen.\n\nWissenswert:\nEin Blatt reicht für eine Kanne aus. Nicht mehr als 10 Sekunden ziehen lassen, da der Tee schnell bitter wird.",                beschreibungen = "Taste:\nSlightly bitte, smoky, gets earthy later on.\n\nHistory:\nIs quite similar to Pu-Erh-Tea. The tea leafs are bundled into banana leafs, reminding a braid. This part of the culture of Chinese tea comes from Zhuang Yuan Fen.\n\nNice to know:\nOne leaf is enough for a whole pot. Be careful, brewing time is really short, not more than 10 seconds." } )endfunction sprachwahl ( valu )  config.diesprache = valu  if (config.diesprache == "de") then    uhrantippen      = "Tippe zum Starten die Uhr an"    tippstop         = "Tippe zum Abbrechen die Uhr an"    istfertig        = "ist fertig"    danke            = "Danke!"    schwarzertee     = "Schwarztee"    gruenertee       = "Grüntee"    mischung         = "Mischung"    teeauswahl       = "Die Teeauswahl"    tabs             = {"Uhr", "Teewahl", "Einstellungen"}    alarme           = {"Alarm", "Hahn", "Elektronisch"}    language         = "Sprache"    sprache          = "Deutsch"    secondstick      = "Sekundenticken"    counttick        = "Countdown"    vibralarm        = "Vibrationsalarm"    weitereoptionen  = "Weitere Optionen"    verschiedenetees = "Diverse Teesorten"    oolongtee		 = "Oolong (Basis: schwarz)"    kraeutertee		 = "Kräutertee"	fruechtetee		 = "Früchtetee"    aufgussgetraenk  = "Diverse Aufgußgetränke"    weiterfuehrend   = "Weiterführende Informationen"    getraenk         = "Tee"    bruehen          = "aufbrühen"    zuruck           = "zurück"    alarmname        = "Alarmton"    aktuellausgewaehlt = "Aktuell ausgewählt:"    teeinfo			 = "Tee-Info"    ziehzeit         = "Ziehzeit"    temperatur		 = "Temperatur"  elseif (config.diesprache == "en") then    uhrantippen      = "Tap the clock to start"    tippstop         = "Tap the clock to stop and cancel"    istfertig        = "is ready"    danke            = "Thanks!"    schwarzertee     = "Black Tea"    gruenertee       = "Green Tea"    mischung         = "Blend"    teeauswahl       = "Choice of Tea"    tabs             = {"Clock", "Choice of Tea", "Configuration"}    alarme           = {"Alarm", "Rooster", "Electronic"}    language         = "Language"    sprache          = "English"    secondstick      = "Ticking of Second"    counttick        = "Countdown"    vibralarm        = "Vibration Alert"    weitereoptionen  = "Options"    verschiedenetees = "More types of Tea"    oolongtee		 = "Oolong (Base: black)"    kraeutertee		 = "Herbal infusion"	fruechtetee		 = "Fruits infusion"    aufgussgetraenk  = "Hot infusion preparations"    weiterfuehrend   = "More Information"    getraenk         = "Tea"    zuruck           = "Back"    bruehen          = "Brew"    alarmname        = "Alarmsound"    aktuellausgewaehlt = "Current Choice:"    teeinfo			 = "Tea-Info"    ziehzeit         = "Brewing Time"    temperatur		 = "Temperature"  end    tabslang  = tabs  mache_tee ( )  einstellung_speichern ()endfunction loadScreen ( newScreen )	if currentScreen then    	transition.to ( currentScreen, { time=250, alpha=0 } )    	currentScreen:cleanUp()  	end  	currentScreen = require(newScreen).new()  	currentScreen.alpha = 0  	tabView:insert( currentScreen )  	transition.to ( currentScreen, { time=250, alpha=1.0 } )  	return trueendfunction showScreen(event)  local t = event.target  if event.name == "tabButtonPress" then     if (t.id == 1) and (lasttab ~= 1) then      lasttab = 1      erstertab = t      loadScreen("screen1") -- UHR    elseif (t.id == 2) and (lasttab ~= 2)  then      lasttab = 2      loadScreen("screen2")   -- Tee    elseif (t.id == 3) and (lasttab ~= 3) then      lasttab = 3      loadScreen("screen3")  -- Einstellungen    end  end  return trueendfunction goback ( event )  if tabBar.y ~= display.contentHeight then    transition.to(tabBar, {time=400, x=screenW / 2, transition=easing.outExpo })  end  lasttab = 1  loadScreen ( "screen1" )endfunction zeige_tabbar ( event )  	transition.to( tabBar, { time=350, x=screenW / 2, y=display.contentHeight - tabBar.height / 2 + 1 } )endfunction hole_tabbar ( )	if tabBar then		display.remove ( tabBar )		tabBar = nil	end	tabButtons = {		{ id=1, label="", up="gui/tab1.png", down="gui/tab1_over.png", width=40, height=40, onPress=showScreen, selected=true },		{ id=2, label="", up="gui/tab2.png", down="gui/tab2_over.png", width=40, height=40, onPress=showScreen },		{ id=3, label="", up="gui/tab3.png", down="gui/tab3_over.png", width=40, height=40, onPress=showScreen },    }    tabBar = widget.newTabBar{ top=480, buttons=tabButtons }	timer.performWithDelay ( 350, zeige_tabbar )end  einstellung_laden ()  sprachwahl ( config.diesprache )  -- Sprache einstellen--Create a group that contains the entire screen and tab bar  mainView = display.newGroup()  --Create a group that contains the screens beneath the tab bar  tabView = display.newGroup()    mainView:insert(tabView)-- add advertising	if ( system.getInfo("platformName") == "Android" ) then		ads.init( "inneractive", "quadWorks_TeeTimer_Android" )	else		if ( system.getInfo("model") == "iPad" ) then			ads.init( "inneractive", "quadWorks_TeeTimer_iPad" )		else			ads.init( "inneractive", "quadWorks_TeeTimer_iPhone" )		end	end   loadScreen("screen1")  hole_tabbar ()-- Memory Leaks findenlocal monitorMem = function()	collectgarbage()	print( "MemUsage: " .. collectgarbage("count") )	local textMem = system.getInfo( "textureMemoryUsed" ) / 1000000	print( "TexMem:   " .. textMem )end--Runtime:addEventListener( "enterFrame", monitorMem )